import type { UseDateProps } from "@/types/UseDateProps";

export default function useDate() {
    function today({ locale = "pt-BR", payload }: UseDateProps = {}) {
        let options = payload ?? { day: "2-digit", month: "2-digit", year: "2-digit" };

        return new Date().toLocaleDateString(locale, options)
    }

    function format({ date, locale = "pt-BR", payload }: UseDateProps = {}): string {
        if (!date) throw new Error(`invalid date: ${date}`);

        const options: Intl.DateTimeFormatOptions = payload ?? {
            day: "2-digit",
            month: "2-digit",
            year: "2-digit",
        };

        return date.toLocaleDateString(locale, options);
    }

    return { today, format }
}

import type { UseDomProps } from "@/types/UseDomProps";

export default function useDom<T extends HTMLElement>(selector: string): UseDomProps<T> {
    const ref: UseDomProps<T> = {
        current: null,

        set(selector: string | T) {
            if(typeof selector === "string") {
                const element = document.querySelector<T>(selector);
    
                if(!element) throw new Error(`${selector} not found.`);
    
                this.current = element;
            } else {
                this.current = selector;
            }
        },

        on(type, handler) {
            if(!this.current) return;
            this.current.addEventListener(type, handler as EventListener);
        },

        off(type, handler) {
            if(!this.current) return;
            this.current.removeEventListener(type, handler as EventListener);
        }
    }

    if(selector) ref.set(selector);

    return ref;
}

import env from "@/constants/env";
import type { CacheItem, UserFetchProps, UserFetchResponseProps } from "@/types/UseFetchProps";

const cache = new Map<string, CacheItem<any>>();

export async function useFetch<T = any>(input: string | UserFetchProps): Promise<UserFetchResponseProps<T>> {
    let data  = null;
    let error = null;
    let payload = {
        endpoint: input,
        method: "GET",
        headers: { "Content-Type": "application/json" },
        body: undefined
    } as UserFetchProps;

    if(typeof input !== "string") payload = {...payload, ...input};

    if(!payload.endpoint) return { data: null, error: "invalid endpoint" };

    const cacheKey = `${payload.method}:${payload.endpoint}`;
    
    if (payload.method?.toUpperCase() === "GET" && cache.has(cacheKey)) {
        const cached = cache.get(cacheKey)!;

        if (Date.now() - cached.timestamp < 5 * 60 * 1000) {
            return cached.result;
        } else {
            cache.delete(cacheKey);
        }
    }

    try {
        const options: RequestInit = {
            method: payload.method!.toUpperCase(),
            headers: payload.headers,
        };

        if (payload.body) {
            options.body = typeof payload.body === "string" ? payload.body : JSON.stringify(payload.body);
        }

        const response = await fetch(env.api + payload.endpoint, options);
        data = await response.json();
    } catch (err) {
        error = err;
        throw new Error(`Erro: ${err}`);
    }

    const result: UserFetchResponseProps<T> = { data, error };

    if(payload.method?.toUpperCase() === "GET") {
        cache.set(cacheKey, { result, timestamp: Date.now() });
    }

    return result;
}

import type { RouteListener } from "@/types/UseRouterProps";

export default function useRouter() {
    let current = window.location.pathname;
    const listeners = new Set<RouteListener>();

    function notify(path: string) {
        listeners.forEach((listener) => listener(path));
    }

    function push(path: string) {
        window.history.pushState({}, "", path);

        current = path;

        notify(current);
    }

    function replace(path: string) {
        if(path === current) return;

        window.history.replaceState({}, "", path);

        current = path;

        notify(current);
    }

    function onChange(listener: RouteListener) {
        listeners.add(listener);

        return () => listeners.delete(listener);
    }

    window.addEventListener("popstate", () => {
        current = window.location.pathname;

        notify(current);
    });

    return {
        get path() {
            return current;
        },
        push,
        replace,
        onChange
    }
}

export default function useSelect(selectID: string) {
    const current = document.querySelector<HTMLSelectElement>(selectID);
    if(!current) throw new Error(`selector: ${selectID} not found.`);

    function handle(selector: string, callback: (e: HTMLSelectElement) => void) {
        const select = document.querySelector<HTMLSelectElement>(selector);
        if(!select) throw new Error(`selector: ${selectID} not found.`);
        callback(select);
        return select;
    }

    return { current, handle };
}

export default function useStorage() {
    const get = (key: string) => {
        try {
            const data = localStorage.getItem(key);
            return data ? JSON.parse(data) : null;
        } catch (error) {
            console.warn(`Erro ao ler localStorage para a chave "${key}":`, error);
            return null;
        }
    }

    const set = (key: string, value: Object): boolean => {
        try {
            const data = JSON.stringify(value);
            localStorage.setItem(key, data);
            return true;
        } catch (error) {
            console.warn(`Erro ao salvar no localStorage para a chave "${key}":`, error);
            return false;
        }
    }

    return { get, set };
}

import type { ErrorsProps, ValidateReturnProps, InputsProps, ValidateProps } from "@/types/UseValidateProps";

function removeClass(element: HTMLElement, classname?: string) {
    if(!classname) return;
    
    element.classList.remove(classname)
}

export default function useValidate<T extends HTMLElement>(selector: string) {
    const wrapper = document.querySelector<T>(selector);

    function validate(options?: ValidateProps): ValidateReturnProps {
        const errors: ErrorsProps[] = [];
        const fields = Array.from(wrapper?.querySelectorAll<InputsProps>('[data-validate="true"]') || []);

        console.log(fields);

        if(!fields) throw new Error("Nenhum campo foi encontrado.");

        fields.forEach((field) => {
            const value = field.value.trim();

            removeClass(field, options?.classError);
            removeClass(field, options?.classValid);

            if(!value) {
                errors.push({ field: field.name || "unknown", message: "Campo obrigatório." });
                options?.classError && field.classList.add(options.classError);
                field.addEventListener("input", () => removeClass(field, options?.classError));
                return;
            }

            options?.classValid && field.classList.add(options.classValid);
        });

        const result = { valid: errors.length === 0, errors };

        if(options?.callback) options.callback(result);

        return result;
    }

    return { validate };
}

import select from "./hooks/useSelect";
import useDom from "./hooks/useDom";
import useValidate from "./hooks/useValidate";
import useSelect from "./hooks/useSelect";

function main() {
    const form = useDom("#form");
    const { validate } = useValidate("#form");

    const { current, handle } = useSelect("#select");

    handle("#select2", (select) => {
        current.addEventListener("change", (e) => {
            const target = e.target as HTMLSelectElement;
            
            select.value = "";
            select.classList.remove("error");

            if(target.value) {
                select.classList.remove("disabled");
                select.setAttribute("data-validate", "true");
            } else {
                select.classList.add("disabled");
                select.setAttribute("data-validate", "false");
            }
        });
    });

    form.on("submit", (e) => {
        e.preventDefault();

        validate({
            classError: "error",
            classValid: "valid"
        });
    });
}

main();

