}

type EventMap = {
    [K in keyof HTMLElementEventMap]?: (this: HTMLElement, ev: HTMLElementEventMap[K]) => any;
};

export interface UseDomProps<T extends HTMLElement> {
    current: T | null,
    set: (selector: string | T) => void,
    on: <K extends keyof HTMLElementEventMap>(type: K, handler: EventMap[K]) => void;
    off: <K extends keyof HTMLElementEventMap>(type: K, handler: EventMap[K]) => void;
}

export interface UserFetchProps {
    endpoint: string,
    method?: "GET" | "POST" | "PUT" | "PATCH" | "DELETE",
    headers?: any,
    body?: any
}

export interface UserFetchResponseProps<T> {
    data: T | null,
    error: any
}

export interface CacheItem<T> {
    result: UserFetchResponseProps<T>;
    timestamp: number;
};

export type ErrorsProps = {
    field: string,
    message: string
}

export interface ValidateProps {
    classError?: string,
    classValid?: string,
    callback?: (arg: any) => void
}

export interface ValidateReturnProps {
    valid: boolean,
    errors: ErrorsProps[]
}

export type InputsProps = HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement;

